package com.example.project.model;

import javax.persistence.*;

@Entity
@Table(name = "users")
//These annotations mark the class as a JPA entity and specify the database table name.

public class User {
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;
//@Id and @GeneratedValue: These annotations indicate that the 'id' field is the primary key and will be auto-generated by the database.
    @Column(unique = true, nullable = false)
    private String username;
//@Column: Specifies column properties, such as uniqueness and nullability, for the 'username' and 'password' fields.
    @Column(nullable = false)
    private String password;


// Getters and Setters

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getUsername() {
        return username;
    }

    public void setUsername(String username) {
        this.username = username;
    }

    public String getPassword() {
        return password;
    }

    public void setPassword(String password) {
        this.password = password;
    }

    public UserProfile getUserProfile() {
        return userProfile;
    }

    public void setUserProfile(UserProfile userProfile) {
        this.userProfile = userProfile;
    }
    // ERD Relationships
    // One-to-One relationship with user profile (optional)
    @OneToOne(mappedBy = "user")
    private UserProfile userProfile;

    // to String
    //@Override annotation is used to indicate that a method in a subclass is intended to override a method with the same name, return type, and parameter list in its superclass (parent class). When you mark a method with @Override, you're essentially telling the compiler that you intend to provide a new implementation for a method that's already defined in the superclass.
    @Override
    public String toString() {
        return "User{" +
                "id=" + id +
                ", username='" + username + '\'' +
                ", password='" + password + '\'' +
                ", userProfile=" + userProfile +
                '}';
    }
}
